#ifndef SERVOMOTOR_H
#define SERVOMOTOR_H

// Detect if we are compiling for real Arduino hardware vs desktop
#if defined(ARDUINO) && (ARDUINO >= 100)
  // If actual Arduino environment is present, use the real Arduino core
  #include <Arduino.h>
#else
  // Otherwise, use our desktop emulator
  #include "ArduinoEmulator.h"
#endif

// Bring in the auto-generated conversions 
#include "AutoGeneratedUnitConversions.h"

// Bring in motor commands or servo class 
#include "ServoMotorCommands.h"  // your existing servo code
#include "Motor.h"       // your simpler Motor class, if desired

/*
  Optionally, you can wrap everything into a single 
  “Servomotor” class or namespace here, or you can 
  simply re-expose the existing classes directly.

  For example:

  namespace ServomotorLibrary {
    using ::ServoMotor;
    using ::Motor;
    // etc.
  }

  That way, a user includes <Servomotor.h> and 
  can do:

    ServomotorLibrary::ServoMotor servo1(...);

  or:

    ServomotorLibrary::Motor motor1(...);
*/

#endif // SERVOMOTOR_H
